Ext.Loader.setConfig({
    enabled: true
});

Ext.application({

    requires: [
        'Ext.ux.data.PagingMemoryProxy',
        'Ext.grid.Panel',
        'Ext.data.reader.Json',
        'Ext.toolbar.Paging',
        'Ext.data.proxy.Rest'
    ],
    models: [
        'ModelAdminUser'
    ],
    stores: [
        'StoreAdminUsers'
    ],
    views: [
        'MainViewport',
        'LoginWindow'
    ],
    autoCreateViewport: true,
    name: 'App',
    controllers: [
        'UserInvoiceArticleController',
        'LoginController',
        'EmailController'
    ],

    launch: function() {
        var myMask = new Ext.LoadMask(Ext.getBody(), 
        {msg:"Checking If Logged In..."});
        var task = new Ext.util.DelayedTask(function(){
            myMask.show();
        });

        task.delay(500);
        Ext.Ajax.request({ 
            url:'/rpc/Account/IsLoggedIn', 
            params: {},
            method: 'POST',
            success: function(r, o) { 
                task.cancel();
                if (myMask.isVisible) {
                    myMask.hide();
                }
            },
            failure: function(r,o) {
                task.cancel();
                if (myMask.isVisible) {
                    myMask.hide();
                }  

                var loginWindow = Ext.create('App.view.LoginWindow',{});
                loginWindow.showAt(50,130,true);

            }
        });
    }

});
