Ext.define('RegistrationApp.controller.SpeakerAfterLoginController', {
    extend: 'Ext.app.Controller',

    onContinueButtonIdClick: function(button, e, eOpts) {
        var myMask = new Ext.LoadMask(Ext.getBody(), {msg:"Updating..."});
        myMask.show();

        var tabPanel = Ext.ComponentQuery.query('tabWizardPanelAlias')[0];
        var thisPanel = Ext.getCmp("speakerAfterLoginProfileId");

        //debugger;
        Ext.Ajax.request({ 
            url:'/rpc/Account/UpdateSpeaker', 
            actionMethods:'POST', 
            scope:this, 
            params: thisPanel.getForm().getValues(),
            success: function(r, o) {  
                // debugger;
                tabPanel.setActiveTab(tabPanel.getTabIdByName('SpeakerSessionUpdate'));
                myMask.hide();
            },
            failure: function(r,o) {
                //debugger;
                tabPanel.setActiveTab(tabPanel.getTabIdByName('AttendeeSpeakerSponsorId'));
                myMask.hide();
            } 
        });

    },

    onLogoutButtonIdClick: function(button, e, eOpts) {
        var myMask = new Ext.LoadMask(Ext.getBody(), {msg:"Logging Out..."});
        myMask.show();

        Ext.Ajax.request({ 
            url:'/rpc/Account/LogOut', 
            actionMethods:'POST', 
            scope:this, 
            params:{
                Username: '',
                Password: '',
                RememberMe: true
            },
            success: function(r, o) { 
                var tabPanel = Ext.ComponentQuery.query('tabWizardPanelAlias')[0];
                tabPanel.setActiveTab(tabPanel.getTabIdByName('attendeeorspeaker'));
                console.log('setting tab to attendeeorspeaker  logout succeeded');
                myMask.hide();
            },
            failure: function(r,o) {
                var tabPanel = Ext.ComponentQuery.query('tabWizardPanelAlias')[0];
                tabPanel.setActiveTab(tabPanel.getTabIdByName('attendeeorspeaker'));
                console.log('setting tab to attendeeorspeaker. logout failed');
                myMask.hide();
            } 

        });
    },

    onEditSelectedSessionButtonIdClick: function(button, e, eOpts) {
        //debugger;

        var sessionsSpeakerPanel = Ext.getCmp("sessionsBySpeakerGridPanelId");
        var selectedRecs = sessionsSpeakerPanel.getSelectionModel().getSelection();
        if (selectedRecs.length > 0) {
            Ext.create('RegistrationApp.view.WindowSession',{
            }).show();
        }
        else {
            Ext.Msg.alert('Must Have Current Sessions and Select One In Order To Edit');   
        }
    },

    onAddNewSessionButtonIdClick: function(button, e, eOpts) {

        var sessionsSpeakerPanel = Ext.getCmp("sessionsBySpeakerGridPanelId");
        var store = sessionsSpeakerPanel.getStore();
        var newRecord = Ext.create('RegistrationApp.model.Session',{
            title: 'my title',
            description: 'my descr'
        });
        store.add(newRecord);

        //debugger;

        Ext.create('RegistrationApp.view.WindowSession',{
        }).show();
    },

    init: function(application) {
        this.control({
            "SpeakerAfterLoginAlias2 #continueButtonId": {
                click: this.onContinueButtonIdClick
            },
            "SpeakerAfterLoginAlias2 #logoutButtonId": {
                click: this.onLogoutButtonIdClick
            },
            "#editSelectedSessionButtonId": {
                click: this.onEditSelectedSessionButtonIdClick
            },
            "#AddNewSessionButtonId": {
                click: this.onAddNewSessionButtonIdClick
            }
        });
    }

});
