Ext.define('RegistrationApp.controller.SponsorController', {
    extend: 'Ext.app.Controller',

    onContinueButtonIdClick: function(button, e, eOpts) {
        var form =  Ext.ComponentQuery.query('sponsorAlias')[0].getForm();

        //debugger;
        if (form.isValid())
        {

            var myMask = new Ext.LoadMask(Ext.getBody(), {msg:"Processing..."});
            myMask.show();
            form.submit({
                success: function(form, r) {
                    myMask.hide();
                    Ext.Msg.alert('Success', 'Thanks for filling out the info. We will be in touch soon.');
                    window.parent.location.href = '../../Register.aspx'; 
                },
                failure: function(form, r) {
                    myMask.hide();
                    var retData = Ext.JSON.decode(r.response.responseText);
                    Ext.Msg.alert('Failed', retData.message);
                }
            });
        }

    },

    onBackButtonIdClick: function(button, e, eOpts) {
        var tabWizardPanel = Ext.getCmp('TabWizardId');
        tabWizardPanel.setActiveTab(tabWizardPanel.getTabIdByName('AttendeeSpeakerSponsorId'));
    },

    init: function(application) {
        this.control({
            "sponsorAlias #continueButtonId": {
                click: this.onContinueButtonIdClick
            },
            "sponsorAlias #backButtonId": {
                click: this.onBackButtonIdClick
            }
        });
    }

});
